INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('1', 'Première étape', 'Blanchir les jaunes et le sucre au fouet et détendre le mélange avec un peu d\'eau.', '1', '1');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('2', 'Deuxième étape', 'Mélanger au doigt la farine et le beurre coupé en petites parcelles pour obtenir une consistance sableuse et que tout le beurre soit absorbé (!!! Il faut faire vite pour que le mélange ne ramollisse pas trop!). ', '2', '1');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('3', 'Troisième étape', 'Verser au milieu de ce \"sable\" le mélange liquide. Incoporer au couteau les éléments rapidement sans leur donner de corps. ', '3', '1');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('4', 'Quatrième étape', 'Former une boule avec les paumes et fraiser 1 ou 2 fois pour rendre la boule + homogène. Foncez un moule de 25 cm de diamètre avec la pâte, garnissez la de papier sulfurisé et de haricots secs. ', '4', '1');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('5', 'Cinquième étape : Crème de citron', 'CRÈME AU CITRON : Laver les citrons et en \"zester\" 2. Mettre les zestes très fins dans une casserole. Presser les citrons et mettre le jus avec les zestes dans la casserole. Verser le sucre et la Maïzena. Remuer, et commencer à faire chauffer à feux doux. Battre les oeufs dans un récipients séparé. Une fois les oeufs battus, incorporer tout en remuant le jus de citron, le sucre, la Maïzena et les zestes. Mettre à feu fort et continuer à remuer à l\'aide d\'un fouet. Le mélange va commencer à s\'épaissir. Attention de toujours remuer lorsque les oeufs sont ajoutés, car la crème de citron pourrait brûler. Oter du feux et verser l\'appareil sur le fond de tarte cuit. ', '5', '1');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('6', 'Sixième étape : Meringue', 'MERINGUE : Monter les blancs en neige avec une pincée de sel. Quand ils commencent à être fermes, ajouter le sucre puis la levure. Mixer jusqu\'à ce que la neige soit ferme. Recouvrir avec les blancs en neige et napper. Cuire à four doux (120°C/150°C) juqu\'à ce que la meringue dore (10 mn) ', '6', '1');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('7', 'Préparation des pâtes', 'Dans une grande casserole d\'eau, faites cuire les pâtes, ajouter l\'huile. Vérifier la cuisson 10 à 15 mn. Une fois cuites les passer sous l\'eau froide. ', '1', '2');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('8', 'La sauce', 'Egoutter le thon et l\'émietter grossièrement. Mélanger la mayonnaise, le jus de citron et le curry. ', '2', '2');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('9', 'Melange', 'Couper les asperges en tiges de 3 à 4 cm. Dans un saladier, verser les pâtes, le thon, les asperges, puis la sauce. Attention, mélanger doucement pour ne pas abimer les asperges. Goûter, si il vous semble que cela manque de curry, ajouter en un peu (par petites doses !!).\n\nServir frais. ', '3', '2');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('10', 'Les escalopes', 'Découper les escalopes congeleés en fines lamelles (les congeler facilite la découpe et permet de faire de belles lamelles !). ', '1', '3');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('11', 'Le beurre et l\'ail', 'Faites fondre la noix de beurre dans une poêle à fond épais. Quand le beurre cesse de chanter, faites saisir l\'ail pendant 30 s. ', '2', '3');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('12', 'La cuisson', 'Ajouter les escalopes. et faites-les bien dorer. Vous pouvez ajouter des lamelles de champignons de Paris que vous faites griller, ça donne un bon goût. ', '3', '3');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('13', 'Ajout de sauce', 'Ajouter une bonne cuillère à soupe de sauce soja, remuer, et laisser les lamelles de viande s\'en imprégner pendant 1 mn. Ajouter la crème, remuer, et c\'est prêt !', '4', '3');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('14', 'Bonus', 'Vous pouvez ajouter quelques brins de persil et accompager cette préparation avec du riz ou ce que vous voulez ! Bon appétit !', '5', '3');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('15', 'Les moules', 'Ecraser les petits beurre en grosses miettes dans 1 saladier, y verser dessus le beurre préalablement fondu, mélanger le tout. Ajouter 2 cuillères à soupe de sucre roux et mélanger. Etaler cette préparation dans un moule anti-adhésif (moule à manqué c\'est bien). Réserver au frigo en attendant. ', '1', '4');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('16', 'La mixture', 'Mélanger les jaunes d\'oeufs et le sucre (pour moi environ 100 g) jusqu\'à ce que le mélange devienne mousseux et clair (au batteur électrique c\'est plus rapide...). Ajouter le zeste et le jus du 1/2 citron. Ensuite mélanger avec le fromage blanc et les deux petits suisses. Puis mettre la farine et rendre la préparation homogène. Monter les blancs en neige bien ferme et les incorporer délicatement à la préparation. ', '2', '4');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('17', 'La cuisson', 'Verser doucement dans le moule tapissé de petits beurres. Mettre au four pendant 45 mn à 1 heure (cela dépend du four et du la teneur en eau du fromage blanc) Th 150°C ou 4 . Quand c\'est cuit (pâte ferme, mousseuse et légèrement colorée) mettre au frais avant de déguster.', '3', '4');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('18', 'Début de sauce et lapin', 'Préchauffez le four à 180°C (thermostat 6). Déposez les lardons dans une casserole, couvrez d’eau froide à hauteur, puis portez à ébullition, égouttez et refroidissez. Otez l’os de la partie haute des cuisses. Pour cela ouvrez en portefeuille de part et d’autre de l’os et dégagez-le. Salez, poivrez, puis déposez une petite branche de thym et une tomate confite. Refermez et ficelez. Garnissez les râbles de la même façon, rabattez les flans l’un sur l’autre et ficelez.', '1', '5');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('19', 'Fin de lapin et cuisson', 'Salez légèrement, poivrez le tout, et faites-les colorer dans une cocotte chaude avec une cuillère à soupe de Lesieur Duo Huile & Beurre. Ajoutez les lardons, les gousses d’ail lavées non-épluchées et les échalotes taillées en quatre dans le sens de la longueur. Couvrez et enfournez pour 40 minutes à Th 5/6. A mi-cuisson découvrez et terminez la cuisson ainsi.', '2', '5');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('20', 'Fin de cuisson et accompagnement', 'Pendant ce temps, lavez les pommes de terre sans les éplucher, taillez-les en 6 dans le sens de la longueur et faites-les sauter dans une poêle chaude avec 2 cuillères à soupe de Lesieur Duo Huile & Beurre. Au moment de servir, ajoutez dans la cocotte les feuilles d’épinards lavées, équeutées, et mélangez délicatement, puis rectifiez l’assaisonnement. Accompagnez des pommes rissolées, parsemées de fleur de sel et de ciboulette ciselée.', '3', '5');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('21', 'Préparation ramequins', 'Tapissez l\'intérieur de 4 ramequins de film alimentaire. ', '1', '6');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('22', 'La crème', 'Dans un saladier, mettez la crème glacée Carte d\'Or et émiettez les 4 meringues. Ajoutez la moitié des fruits rouges et mélangez délicatement. ', '2', '6');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('23', 'La crème : suite', 'Versez ce mélange dans les 4 ramequins, lissez la surface, recouvrez de film alimentaire et placez au congélateur pour une heure de repos. ', '3', '6');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('24', 'Le coulis', 'Pendant ce temps, mettez le reste des fruits rouges dans un mixeur avec le miel et mixez jusqu\'à obtenir un coulis. ', '4', '6');
INSERT INTO `luto`.`step` (`step_id`, `step_title`, `step_description`, `step_order`, `recipe_id`) VALUES ('25', 'Démoulage', 'Au moment de servir, démoulez précautionneusement les ramequins sur des assiettes à dessert, nappez de coulis aux fruits rouges, décorez d\'une cerise et dégustez. ', '5', '6');
